import { FETCH_POSTS, NEW_POST } from "../actions/Types";

const initialState = {
    items: [],
    item: {}
}

export default function(state = initialState, action) {
    switch(action.type) {
        case FETCH_POSTS:
            return {
                ...state,
                items: action.payload
            }
        case NEW_POST:
            console.log("and here")
            return {
            ...state,
            item: action.payload
            }

        default:
            return state;
    }
}
