import { FETCH_POSTS, NEW_POST } from "./Types";

export function fetchPosts() {
    return function(dispatch) {
        console.log("fetching")
        fetch('https://jsonplaceholder.typicode.com/posts')
        .then(res => res.json())
        .then(posts => dispatch({
            type: FETCH_POSTS,
            payload: posts
        }));
    }
}
